import {useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "./components/Product";
import { fetchProducts, searchProduct, sortProduct, sortProductByPrice } from "./store/products";
import {
  selectProducts,
  selectProductsLoading
} from "./store/products/selectors";
import "./App.css";
import { BrowserRouter, Routes, Route,useNavigate } from "react-router-dom";
import ProductDetails from "./components/ProductDetails";
import { Dropdown } from "react-bootstrap";

export default function App() {
  const data = useSelector(selectProducts);
  const loading = useSelector(selectProductsLoading);
  const dispatch = useDispatch();
  const handleFetchProducts = () => {
    dispatch(fetchProducts());
  };

  const navigate = use



  //Search functions...
  const handleSearch = (e) => {
    e.preventDefault();
    const searchTerm = e.target.value;
    if (!searchTerm) {
      handleFetchProducts();
      return;
    }
    dispatch(searchProduct(searchTerm));
  };

  //Sort functions..
const [isAscending, setIsAscending] = useState(false)

  const handleSort = () => {
    dispatch(sortProduct(isAscending));
    // const sortedproducts = isAscending
    //   ? sortAscending(products)
    //   : sortDescending(products);
    setIsAscending(!isAscending);
    // setProducts(sortedproducts);
  };

  // const handleSortByPrice = () =>{
  //   dispatch(sortProductByPrice(isAscending));
  //   setIsAscending(!isAscending);

  // }

  //fetch products on componenet mount
  useEffect(() => {
    handleFetchProducts();
    handleSort();
    // handleSortByPrice();
  }, []);

  if (loading) return "Loading....";

  return (
    <>
    <BrowserRouter>

    <div className="menu">
    <div className="ui container center">
        <h2>MyStore</h2>
    </div>
    <div className="row justify-content-md-center">
     <div className="col col-lg-1">

     <button className="sortbutton" onClick={handleSort}> 
    Sort title
    </button>
    </div>
    <div className="col-md-auto " >
    <div className="pricebutton " >
      <select className ="p-2 mb-2 bg-warning text-dark">
         <option value="normal">Sort Price</option>
        <option value="lowtohigh">Low to High</option>
        <option value="hightolow">High to Low</option>

      </select>

     </div>
     </div> 

    </div>


      {/* <Dropdown.Toggle variant="success" id="dropdown-basic">
        Sort Price
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1" onClick={() => this.handleSort()}>low to high</Dropdown.Item>
        <Dropdown.Item href="#/action-2" onClick={() => this.handleSort()}>high to low</Dropdown.Item>
        <Dropdown.Item href="#/action-3" onClick={() => this.handleSort()}> mid price</Dropdown.Item>
      </Dropdown.Menu> */}


    </div>
    <Routes><Route path= '/' element = {
    <div className="container">
      <div className="d-fex p-2">
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="input-group mb-3">
          <input
            type="search"
            className="form-control"
            placeholder="search"
            onChange={handleSearch}
          />
          <button type="submit" className="btn btn-dark">
            search
          </button>
        </div>

      </form>
      <div className="d-fex p-2">
        <div className="row mb-2">
          <div className="col md-6">
        {data.map((pd) => (
          <Product product={pd} key={pd.id} />

        ))}
        </div>
      </div>
        </div>
      </div>
      </div>

    }></Route>
    <Route path ="/product/:productId"  element= {ProductDetails}/>
    </Routes>

    </BrowserRouter>
</> 
  );
}
