import {useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "./components/Product";
import { fetchProducts, searchProduct, sortProduct, sortProductByPrice } from "./store/products";
import {
  selectProducts,
  selectProductsLoading
} from "./store/products/selectors";
import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import ProductDetails from "./components/ProductDetails";
import { Dropdown } from "react-bootstrap";

export default function App() {
  const data = useSelector(selectProducts);
  const loading = useSelector(selectProductsLoading);
  const dispatch = useDispatch();
  const handleFetchProducts = () => {
    dispatch(fetchProducts());
  };


  //Search functions...
  const handleSearch = (e) => {
    e.preventDefault();
    const searchTerm = e.target.value;
    if (!searchTerm) {
      handleFetchProducts();
      return;
    }
    dispatch(searchProduct(searchTerm));
  };

  //Sort functions..
const [isAscending, setIsAscending] = useState(false)

  const handleSort = () => {
    dispatch(sortProduct(isAscending));
    // const sortedproducts = isAscending
    //   ? sortAscending(products)
    //   : sortDescending(products);
    setIsAscending(!isAscending);
    // setProducts(sortedproducts);
  };

  // const handleSortByPrice = () =>{
  //   dispatch(sortProductByPrice(isAscending));
  //   setIsAscending(!isAscending);

  // }

  //fetch products on componenet mount
  useEffect(() => {
    handleFetchProducts();
    handleSort();
    // handleSortByPrice();
  }, []);

  if (loading) return "Loading....";

  return (
    <>
    <BrowserRouter>

    <div className="menu">
    <div className="ui container center">
        <h2>MyStore</h2>
    </div>
    <button className="sortbutton" onClick={handleSort}> 
    Sort title
    </button>

      <form onSubmit={this.handleSubmit}>
        <label>
          Pick your favorite flavor:
          <select value={this.state.value} onChange={this.handleChange}>            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option value="coconut">Coconut</option>
            <option value="mango">Mango</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>





    {/* <button className="pricebutton" > 
    Sort Price
    </button>
     */}
    {/* <div className="pricebutton">
      <select>
         <option value="normal"></option>
        <option value="lowtohigh">Low to High</option>
        <option value="hightolow">High to Low</option>

      </select> */}
      {/* <Dropdown.Toggle variant="success" id="dropdown-basic">
        Sort Price
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1" onClick={() => this.handleSort()}>low to high</Dropdown.Item>
        <Dropdown.Item href="#/action-2" onClick={() => this.handleSort()}>high to low</Dropdown.Item>
        <Dropdown.Item href="#/action-3" onClick={() => this.handleSort()}> mid price</Dropdown.Item>
      </Dropdown.Menu> */}
      {/* </div>  */}

    </div>
    <Routes><Route path= '/' element = {
    <div className="container p-5">
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="input-group mb-3">
          <input
            type="search"
            className="form-control"
            placeholder="search"
            onChange={handleSearch}
          />
          <button type="submit" className="btn btn-dark">
            search
          </button>
        </div>

      </form>
      <div className="row">
        {data.map((pd) => (
          <Product product={pd} key={pd.id} />

        ))}
      </div>
    </div>}></Route>
    <Route path ="/product/:productId"  element= {ProductDetails}/>
    </Routes>

    </BrowserRouter>
</> 
  );
}
