import {useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "./components/Product";
import { fetchProducts, searchProduct, sortProduct } from "./store/products";
import {
  selectProducts,
  selectProductsLoading
} from "./store/products/selectors";
import "./App.css";
import Dropdown from 'react-bootstrap/Dropdown';

export default function App() {
  const data = useSelector(selectProducts);
  const loading = useSelector(selectProductsLoading);
  const dispatch = useDispatch();
  const handleFetchProducts = () => {
    dispatch(fetchProducts());
  };


  //Search functions...
  const handleSearch = (e) => {
    e.preventDefault();
    const searchTerm = e.target.value;
    if (!searchTerm) {
      handleFetchProducts();
      return;
    }
    dispatch(searchProduct(searchTerm));
  };

  //Sort functions..
const [isAscending, setIsAscending] = useState(false)

  const handleSort = () => {
    dispatch(sortProduct(isAscending));
    // const sortedproducts = isAscending
    //   ? sortAscending(products)
    //   : sortDescending(products);
    setIsAscending(!isAscending);
    // setProducts(sortedproducts);
  };

  //fetch products on componenet mount
  useEffect(() => {
    handleFetchProducts();
    handleSort();
  }, []);

  if (loading) return "Loading....";

  return (
    <>


    <div className="menu">
    <div className="ui container center">
        <h2>MyStore</h2>
    </div>
    <button className="btn btn-primary" onClick={handleSort}> 
    Sort title
    </button>
   {/* <div className="dropdown">
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Sort Price
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1" onClick={() => this.handleSort()}>low to high</Dropdown.Item>
        <Dropdown.Item href="#/action-2" onClick={() => this.handleSort()}>high to low</Dropdown.Item>
        <Dropdown.Item href="#/action-3" onClick={() => this.handleSort()}> mid price</Dropdown.Item>
      </Dropdown.Menu>
      </div> */}

    </div>

    <div className="container p-5">
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="input-group mb-3">
          <input
            type="search"
            className="form-control"
            placeholder="search"
            onChange={handleSearch}
          />
          <button type="submit" className="btn btn-primary">
            search
          </button>
        </div>

      </form>
      <div className="row">
        {data.map((pd) => (
          <Product product={pd} key={pd.id} />
        ))}
      </div>
    </div>

</> 
  );
}
