import {useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "./components/Product";
import { fetchProducts, searchProduct, sortProduct, sortProductByPrice } from "./store/products";
import {
  selectProducts,
  selectProductsLoading
} from "./store/products/selectors";
import 'semantic-ui-css/semantic.min.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import "./App.css";
import { BrowserRouter,Switch, Route,Routes } from "react-router-dom";
import ProductDetails from "./components/ProductDetails";
import 'bootstrap/dist/css/bootstrap.min.css';


export default function App() {
  const data = useSelector(selectProducts);
  const loading = useSelector(selectProductsLoading);
  const dispatch = useDispatch();
  const handleFetchProducts = () => {
    dispatch(fetchProducts());
  };

  // const navigate = useNavigate();

  // const navigateToProducts = () => {
  //   navigate(/./ProductDetails);
  // }



  //Search functions...
  const handleSearch = (e) => {
    e.preventDefault();
    const searchTerm = e.target.value;
    if (!searchTerm) {
      handleFetchProducts();
      return;
    }
    dispatch(searchProduct(searchTerm));
  };

  //Sort functions..
const [isAscending, setIsAscending] = useState(false)

  const handleSort = () => {
    dispatch(sortProduct(isAscending));
    // const sortedproducts = isAscending
    //   ? sortAscending(products)
    //   : sortDescending(products);
    setIsAscending(!isAscending);
    // setProducts(sortedproducts);
  };

  // const handleSortByPrice = () =>{
  //   dispatch(sortProductByPrice(isAscending));
  //   setIsAscending(!isAscending);

  // }

  //fetch products on componenet mount
  useEffect(() => {
    handleFetchProducts();
    handleSort();
    // handleSortByPrice();
  }, []);

  if (loading) return "Loading....";

  return (
    <>


    <div className="menu">
    <div className="ui container center">
      <div className="row">
        <div className="col">
        <h2>MyStore</h2>

        </div>


     <div className="col-md-auto ">

     <button className="sortbutton bg-dark text-light" onClick={handleSort}> 
    Sort title
    </button></div>

    <div className="col col-lg-2 " >

      <select className ="mb-2 bg-dark text-light">
         <option value="normal">Sort Price</option>
        <option value="lowtohigh">Low to High</option>
        <option value="hightolow">High to Low</option>

      </select>
      </div>

      <div className="col col-lg-1 p-2 mb-2" >
        <button className="icon">
      <FontAwesomeIcon icon="fa-sharp fa-solid fa-cart-plus" /></button>
      </div>
     </div>
     </div> 

    </div>


{/*     
       <Dropdown.Toggle variant="success" id="dropdown-basic">
        Sort Price
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1" onClick={() => this.handleSort()}>low to high</Dropdown.Item>
        <Dropdown.Item href="#/action-2" onClick={() => this.handleSort()}>high to low</Dropdown.Item>
        <Dropdown.Item href="#/action-3" onClick={() => this.handleSort()}> mid price</Dropdown.Item>
      </Dropdown.Menu>  */}





    <div className="container">
      <div className="d-fex p-2">
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="input-group mb-3">
          <input
            type="search"
            className="form-control"
            placeholder="search"
            onChange={handleSearch}
          />
          <button type="submit" className="btn btn-dark">
            search
          </button>
        </div>


      </form>
      </div>

        {data.map((pd) => (
          <Product product={pd} key={pd.id}/>


        ))}

        </div>




</> 
  );
}
