import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Product from "./components/Product";
import { fetchProducts, searchProduct } from "./store/products";
import {
  selectProducts,
  selectProductsLoading
} from "./store/products/selectors";
import "./App.css";

export default function App() {
  const data = useSelector(selectProducts);
  const loading = useSelector(selectProductsLoading);
  const dispatch = useDispatch();
  const handleFetchProducts = () => {
    dispatch(fetchProducts());
  };

  //Search functions...
  const handleSearch = (e) => {
    e.preventDefault();
    const searchTerm = e.target.value;
    if (!searchTerm) {
      handleFetchProducts();
      return;
    }
    dispatch(searchProduct(searchTerm));
  };

  //fetch products on componenet mount
  useEffect(() => {
    handleFetchProducts();
  }, []);

  if (loading) return "Loading....";

  return (
    <>
    <div className="menu">
    <div className="ui container center">
        <h2>MyStore</h2>
    </div>
    <div className="dropdown">
<button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" 
data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
Sort Price
</button>
<div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
<a className="dropdown-item" href="/">Low to high</a>
<a className="dropdown-item" href="/">high to low</a>
<a className="dropdown-item" href="#">mid price</a>
</div>
</div>

    </div>

    <div className="container p-5">
      <form onSubmit={(e) => e.preventDefault()}>
        <div className="input-group mb-3">
          <input
            type="search"
            className="form-control"
            placeholder="search"
            onChange={handleSearch}
          />
          <button type="submit" className="btn btn-primary">
            search
          </button>
        </div>
      </form>
      <div className="row">
        {data.map((pd) => (
          <Product product={pd} key={pd.id} />
        ))}
      </div>
    </div>
</>
  );
}
