import React from "react";
import { useDispatch } from "react-redux";
import { deleteProduct } from "../store/products";
import { Link } from "react-router-dom";

const Product = ({ product }) => {
  const { title, price, description, category, image, rating, id } = product;
  const { rate } = rating;
  const dispatch = useDispatch();

  const deleteCurrentProduct = () => {
    dispatch(deleteProduct(id));
  };

  return (
    <>
     <Link to = {`/product/${id}`}>
    <div className="col col-3 pb-5">
   
      <div className="card">
        <img className="card-img-top" src={image} alt="Card image cap" />
      
          <h5 className="card-title">
            {title} - ${price}
          </h5>
          <div className="badge badge-dark text-danger">{category}</div>
          {/* <p className="card-text">{description}</p> */}
          <span className="btn btn-danger" onClick={deleteCurrentProduct}>
            Delete
          </span>
        </div>
        
      </div>
      </Link>
    
    </>
  
   /* <div className = "four wide column" key = {id}> 
   


<Link to = {`/product/${id}`}>
  
<div className="col col-3 pb-5">
            <div className= "ui link cards">
                <div className="card">
                 
                    <div className="image"> <img src={image} alt={title} />
                    </div>  
                        <div className="content">
                            <div className="header">
                            {title}
                            </div>
                            <div className="meta price">${price}</div>
                            <div className="meta badge-dark text-danger">{category}</div>
                            <span className="btn btn-danger" onClick={deleteCurrentProduct}>  Delete
          </span>
                        </div>
                    </div>
                    </div> </div>
               
               
                </Link> </div>  </> */

  );
};

export default Product;
